StructuredTextModel.Text:
// move to internal value
	tmp_Analog_Voltage_Bits:=Analog_O;
	tmp_Analog_Current_Bits:=Analog_OI;


IF Enable THEN
//This function is to monitor the value of the analog voltage that is input to the analog voltage reference terminal FV for frequency reference input.//

	Analog_Voltage:=ScaleTrans(SclIn:=tmp_Analog_Voltage_Bits, X0:=0, Y0:=0, X1:=1023, Y1:=10.9);


//This function is to monitor the value of the analog current that is input to the analog voltage reference terminal FI for frequency reference input.//

	Analog_Current:=ScaleTrans(SclIn:=tmp_Analog_Current_Bits, X0:=0, Y0:=4, X1:=1023, Y1:=23.3);

//Percentage of the analog values
//Voltage

	Analog_Voltage_Percentage:=tmp_Analog_Voltage_Bits/1023*100;

//Current

	Analog_Current_Percentage:=tmp_Analog_Current_Bits/1023*100;


ENO:=TRUE;

ELSE;
	Analog_Voltage:=0;
	Analog_Current:=0;
	
	Analog_Voltage_Percentage:=0;
	Analog_Current_Percentage:=0;
	
ENO:=FALSE;

END_IF;
