StructuredTextModel.Text:
///Status extraction///
	tmp_Status.ch := Status;
	During_Fwd := tmp_Status.bit[0];
	During_Rev := tmp_Status.bit[1];
	Fault := tmp_Status.bit[3];
	Warning := tmp_Status.bit[7];
	Remote := tmp_Status.bit[9];
	Freq_Match := tmp_Status.bit[12];
	Connection_Error := tmp_Status.bit[15];

///Command extraction///
IF Enable THEN;
	tmp_Command.bit[0] := Fwd;
	tmp_Command.bit[1] := Rev;
	ENO:=TRUE;
ELSE
	tmp_Command.bit[0] := FALSE;
	tmp_Command.bit[1] := FALSE;
	ENO:=FALSE;
END_IF;
	tmp_Command.bit[7] := Fault_Reset;
	Command := tmp_Command.ch;
